create table if not exists user_country ( id int auto_increment primary key, name varchar(100) not null ) collate = utf8mb4_unicode_ci;
create table if not exists user ( id int auto_increment primary key, name varchar(255) not null, surname varchar(255) not null, email varchar(255) not null, password varchar(255) not null, date_of_birth date not null, gender enum ('MALE', 'FEMALE') not null, city varchar(255) not null, country_id int not null, profile_pic varchar(255) null, personal_information varchar(255) null, profile_bg_pic varchar(255) null, constraint user_country__fk foreign key (country_id) references user_country (id) );
create table if not exists education ( id int auto_increment primary key, ed_name varchar(255) null, ed_from_date int null, ed_to_date int null, ed_description varchar(255) null, user_id int not null, constraint education__fk foreign key (user_id) references user (id) );
create table if not exists friend_request ( sender_id int not null, receiver_id int not null, id int auto_increment primary key, status enum ('PENDING', 'ACCEPTED', 'REJECTED') null, constraint friend_request_receiver__fk foreign key (receiver_id) references user (id), constraint friend_request_sender__fk foreign key (sender_id) references user (id) );
create table if not exists interests ( id int auto_increment primary key, interest varchar(255) null, user_id int null, constraint user_interests__fk foreign key (user_id) references user (id) );
create table if not exists language ( id int auto_increment primary key, language varchar(255) null, user_id int null, constraint user_language__fk foreign key (user_id) references user (id) );
create table if not exists post ( id int auto_increment primary key, user_id int not null, description text null, img_name varchar(255) null, music_file_name varchar(255) null, like_count int not null, dislike_count int not null, post_datetime datetime null, constraint user_post__fk foreign key (user_id) references user (id) );
create table if not exists commentary ( id int auto_increment primary key, user_id int not null, post_id int not null, commentary_text text not null, constraint commentary_post__fk foreign key (post_id) references post (id), constraint user_commentary__fk foreign key (user_id) references user (id) );
create table if not exists post_like ( id int auto_increment primary key, like_status enum ('LIKE', 'DISLIKE') null, post_id int not null, user_id int not null, constraint postLike_postLike_fk foreign key (post_id) references post (id), constraint post_like_userLike_fk foreign key (user_id) references user (id) );
create table if not exists user_friends ( user_id int not null, friend_id int not null, primary key (user_id, friend_id), constraint friends__fk foreign key (friend_id) references user (id), constraint user__fk foreign key (user_id) references user (id) );
create table if not exists work_experiences ( id int auto_increment primary key, we_name varchar(255) null, we_designation varchar(255) null, we_from_date int null, we_to_date int null, we_city varchar(255) null, we_description varchar(255) null, user_id int not null, constraint work_experiences__fk foreign key (user_id) references user (id) );
create table if not exists user_image ( id int(11) not null auto_increment primary key, image_name varchar(255) not null, user_id int(11) not null, constraint user_user_image__fk foreign key (user_id) references user (id) );

